var EnumRule = Class.bind(null, 'EnumRule', ValidationRule);

EnumRule({
    constructor: function (enums) {
        this.enums = enums;
    },
    validate: function (context, fullData, currentValue, callback) {
        if (this.enums.indexOf(currentValue) !== -1) {
            return callback(true);
        }

        context.errors.push('Field must be one of ', this.enums);
        callback(false);
    }
});