var IntegerRule = Class.bind(null, 'IntegerRule', ValidationRule);

IntegerRule({
    constructor: function (min, max) {
        this.min = min;
        this.max = max;
    },
    validate: function (context, fullData, currentValue, callback) {
        if (currentValue == undefined) {
            return callback(true);
        }

        var integer = parseInt(currentValue);

        if (isNaN(integer) || !isFinite(integer)) {
            context.errors.push('Field must be and integer');
            return callback(false);
        }
        
        if (this.min && this.min > integer) {
            context.errors.push('Field must be bigger or equals to ' + this.min);
            return callback(false);
        }

        if (this.max && this.max < integer) {
            context.errors.push('Field must be smaller or equals to ' + this.min);
            return callback(false);
        }

        callback(true);
    }
});
