var url = require('url');
var UrlRule = Class.bind(null, 'UrlRule', ValidationRule);

UrlRule({
    constructor: function () {
        this.urlRegex = '^(?!mailto:)(?:(?:http|https|ftp)://)(?:\\S+(?::\\S*)?@)?(?:(?:(?:[1-9]\\d?|1\\d\\d|2[01]\\d|22[0-3])(?:\\.(?:1?\\d{1,2}|2[0-4]\\d|25[0-5])){2}(?:\\.(?:[0-9]\\d?|1\\d\\d|2[0-4]\\d|25[0-4]))|(?:(?:[a-z\\u00a1-\\uffff0-9]+-?)*[a-z\\u00a1-\\uffff0-9]+)(?:\\.(?:[a-z\\u00a1-\\uffff0-9]+-?)*[a-z\\u00a1-\\uffff0-9]+)*(?:\\.(?:[a-z\\u00a1-\\uffff]{2,})))|localhost)(?::\\d{2,5})?(?:(/|\\?|#)[^\\s]*)?$';
        this.url = new RegExp(this.urlRegex, 'i');
    },
    validate: function (context, fullData, currentValue, callback) {
        if (typeof currentValue == 'string' &&
            currentValue.length < 2083 &&
            this.url.test(currentValue)) {

            callback(true);
        } else {
            context.errors.push('Value is not an url.');
            callback(false);
        }
    }
});
