var BooleanIfSetRule = Class.bind(null, 'BooleanIfSetRule', ValidationRule);

BooleanIfSetRule({
    constructor: function () {
        
    },
    validate: function (context, fullData, currentValue, callback) {
        if (currentValue === undefined ||
            currentValue === null ||
            typeof currentValue == 'boolean') {
            callback(true);
        } else {
            context.errors.push('Invalid Boolean Value: ' + currentValue);
            callback(false);
        }
    }
});