var DateRule = Class.bind(null, 'DateRule', ValidationRule);

DateRule({
    constructor: function () {
        
    },
    validate: function (context, fullData, currentValue, callback) {
        if (currentValue == null || currentValue == undefined || !isNaN(Date.parse(currentValue))) {
            callback(true);
        } else {
            context.errors.push('Invalid Date Value: ' + currentValue);
            callback(false);
        }
    }
});