var validator = require('validator');
var EmailRule = Class.bind(null, 'EmailRule', ValidationRule);

EmailRule({
    constructor: function () {
        
    },
    validate: function (context, fullData, currentValue, callback) {
        if (typeof currentValue == 'string') {
            // validate ipv4 or ipv6 address
            var isValid = validator.isEmail(currentValue);

            if (isValid) {
                return callback(true);
            }
        }
        
        context.errors.push('Invalid Email Address: ' + currentValue);
        callback(false);
    }
});