var MacAddressRule = Class.bind(null, 'MacAddressRule', ValidationRule);

MacAddressRule({
    constructor: function () {
        
    },
    validate: function (context, fullData, currentValue, callback) {
        var regex = /^[0-9abcdef]{2}:[0-9abcdef]{2}:[0-9abcdef]{2}:[0-9abcdef]{2}:[0-9abcdef]{2}:[0-9abcdef]{2}$/

        if (typeof currentValue == 'string' && regex.test(currentValue.toLowerCase())) {
            callback(true);
        } else {
            context.errors.push('Invalid Mac Address: ' + currentValue);
            callback(false);
        }
    }
});
