var RequiredRule = Class.bind(null, 'RequiredRule', ValidationRule);

RequiredRule({
    constructor: function () {

    },
    validate: function (context, fullData, currentValue, callback) {
        if (currentValue !== undefined && currentValue !== null) {
            callback(true);
        } else {
            context.errors.push('Field is required');

            callback(false);
        }
    }
});